const cookiePopup = document.getElementById('CookiePopup');
const acceptBtn = document.getElementById('AcceptBtn');
const crossBtn = document.getElementById('btn');

// Function to set the cookie
function setCookie(name, value, days) {
  const expires = new Date();
  expires.setTime(expires.getTime() + (days * 24 * 60 * 60 * 1000));
  document.cookie = name + '=' + value + ';expires=' + expires.toUTCString() + ';path=/';
}

// Function to check if the cookie is set
function checkCookie(name) {
  const cookies = document.cookie.split(';');
  for (let i = 0; i < cookies.length; i++) {
    const cookie = cookies[i].trim();
    if (cookie.startsWith(name + '=')) {
      return true;
    }
  }
  return false;
}

// Function to hide the cookie popup
function hideCookiePopup() {
  cookiePopup.style.display = 'none';
}

// Event listener for the accept button
acceptBtn.addEventListener('click', function (event) {
  event.preventDefault();
  setCookie('cookiePolicyAccepted', 'true', 365);
  hideCookiePopup();
});

// Event listener for the cross button
crossBtn.addEventListener('click', function () {
  hideCookiePopup();
});

// Check if the cookie policy is accepted
if (checkCookie('cookiePolicyAccepted')) {
  hideCookiePopup();
